```kotlin
import androidx.core.content.ContextCompat

fun setupPermissionsRequest(activity: Activity) {
    val requiredPermission = Manifest.permission.WRITE_EXTERNAL_STORAGE
    val shouldShowRequest = ActivityCompat.shouldShowRequestPermissionRationale(
        activity,
        requiredPermission
    )

    when {
        ContextCompat.checkSelfPermission(activity, requiredPermission) == PackageManager.PERMISSION_GRANTED ->
            // Если уже имеется разрешение, то вызовите нужный метод или выполните следующие операции
            createConfigFile(activity, verifiedEmployee, proxySettings)
        shouldShowRequest -> {
            AlertDialog.Builder(activity)
                .setTitle(R.string.title_permission_request)
                .setMessage(R.string.notify_storage_permission)
                .setPositiveButton(R.string.button_continue) { _, _ ->
                    requestStoragePermissions(activity)
                }
                .setNegativeButton(R.string.button_quit) { _, _ ->
                    activity.finishAffinity()
                }
                .show()
        }
        else -> requestStoragePermissions(activity)
    }
}

fun requestStoragePermissions(activity: Activity) {
    ActivityCompat.requestPermissions(
        activity,
        arrayOf(Manifest.permission.WRITE_EXTERNAL_STORAGE),
        PERMISSION_REQUEST_CODE
    )
}


fun createConfigFile(
    context: Context,
    verifiedEmployee: VerifiedEmployee,
    proxySettings: ProxySettings?
) {
    var config = employeeToConfig(verifiedEmployee)

    if (proxySettings != null) {
        config += proxySettingsToConfig(proxySettings)
    }

    mLogger.info { "Config contents created \n$config" }

    val dir = ContextCompat.getExternalFilesDirs(context, "/STMobile/").firstOrNull()
        ?: throw IOException("No available storage found")

    if (dir.exists()) {
        mLogger.info { "Directory $dir already exists. Deleting..." }

        if (!dir.deleteRecursively()) {
            throw IOException("Could not delete directory $dir")
        }
    }

    mLogger.info { "Creating directory $dir..." }

    if (!dir.mkdir()) {
        throw IOException("Could not create directory $dir")
    }

    val configFile = File(dir, "stmobile.conf")

    mLogger.info { "Writing config file $configFile..." }

    configFile.writeText(config)

    mLogger.info { "Config file $configFile written" }
}
