3. **Адаптер** (Adapter)
```javascript
// Пример адаптера для совместимости двух несовместимых интерфейсов
class Class1 {
  constructor() {
    this.value = 10;
  }

  getValue() {
    return this.value;
  }
}

class Class2 {
  constructor() {
    this.number = 5;
  }

  getNumber() {
    return this.number;
  }
}

class Adapter {
  constructor(class2) {
    this.class2 = class2;
  }

  getValue() {
    return this.class2.getNumber();
  }
}

const class1 = new Class1();
const class2 = new Class2();
