// Презентер (Presenter) - класс MoviePresenter
class MoviePresenter {
  constructor(model, view) {
    this.model = model;
    this.view = view;

    // привязка обработчиков событий
    this.view.bindSaveButton(this.handleSaveButton.bind(this));

    // обновление представления с данными модели
    this.updateView();
  }

  // метод обновления представления на основе данных модели
