func (s *RedisStorage) getResults(ctx context.Context, resultID string) error {
	pipeline := s.client.Pipeline()

	totalResult := pipeline.Get(ctx, "key1")
	dailyResult := pipeline.Get(ctx, "key2")
	allResult := pipeline.HGetAll(ctx, "key3")

