```
export const OutstandingTransactionsTable = () => {
  const formatMessage = useFormatMessage();
  const customerId = useTXPStateRead(TransactionSelectors.getContactId);
  const outstandingTableLines =
    useTXPStateRead(paymentTableSelectors.getCharges) || [];
  const onAmountFieldUpdated = useTXPStateWrite(TransactionActions.genericTxnUpdate);
  const onPaymentFieldUpdated = useTXPStateWrite(
    PaymentChargesTableAction.chargesTableDataUpdate
  );

  const Items = (props: { item: OutstandingTableDataTypes; idx: number }) => {
    const { item, idx } = props;

    const txnUrl = getTxnUrl(`${item.txnType.txnTypeId}`, item.txnId);
    const referenceNumber = item?.referenceNumber ? `#${item?.referenceNumber}` : ‘’;
    const amount = useTXPStateRead(TransactionSelectors.getAmount) || 0;

    const { closeTrowser } = useTrowserHooks();

    const [isCheckedWhenPaymentAmount, setIsCheckedWhenPaymentAmount] =
      useState(!!item.linkedPaymentAmount);
    console.log(‘hello2’, isCheckedWhenPaymentAmount);

    const [paymentAmount, setPaymentAmount] =
      useState(item.linkedPaymentAmount);
    console.log(‘hello3’, paymentAmount);

    const handleAmountUpdate = (amountValue: string) => {
      onAmountFieldUpdated({ amount: amountValue });
    };

    const handleCheckboxClicked = (txnId: number) => {
      setIsCheckedWhenPaymentAmount(!isCheckedWhenPaymentAmount);
      console.log(‘hello4’, isCheckedWhenPaymentAmount, !isCheckedWhenPaymentAmount);

      if (!isCheckedWhenPaymentAmount) {
        const value = outstandingTableLines.filter((x) => x.txnId === txnId)[0]
          .openBalance;
        setPaymentAmount(value.toString());
        console.log(‘hello8’, paymentAmount);

        const calcAmount = Number(amount) + Number(value);
        handleAmountUpdate(calcAmount.toString());

        onPaymentFieldUpdated(txnId, {
          linkedPaymentAmount: value.toString(),
          isLinkedPaymentAmount: true,
        });
      } else {
        setPaymentAmount(‘’);

        const value1 = outstandingTableLines.filter((x) => x.txnId === txnId)[0]
          .isLinkedPaymentAmount;
        console.log(‘123’, value1);

        if (value1) {
          const value = outstandingTableLines.filter((x) => x.txnId === txnId)[0]
            .linkedPaymentAmount;

          if (value) {
            const calcAmount = Number(amount) - Number(value);
            handleAmountUpdate(calcAmount.toString());

            onPaymentFieldUpdated(txnId, { linkedPaymentAmount: ‘’ });
          }
        }
      }
    };

    const handlePaymentFieldChange = (
      evt: React.ChangeEvent<HTMLInputElement>
    ) => {
      const value = evt && evt.target.value;

      setPaymentAmount(value);
    };

    const handlePaymentOnBlur = () => {
      setIsCheckedWhenPaymentAmount(!!paymentAmount);

      const oldValue = outstandingTableLines.filter((x) => x.txnId === item.txnId)[0]
        .linkedPaymentAmount;
      onPaymentFieldUpdated(item.txnId, {
        linkedPaymentAmount: paymentAmount,
        isLinkedPaymentAmount: true,
      });

      const oldPaymentAmount = oldValue ? Number(oldValue) : 0;
      const newPaymentAmount = paymentAmount ? Number(paymentAmount) : 0;
      const calcAmount = Number(amount) - oldPaymentAmount + newPaymentAmount;
      handleAmountUpdate(calcAmount.toString());
    };

    const handleNavigate = () =>
      closeTrowser?.({ navigateTo: txnUrl, skipTrowserCloseDelay: true });

    return (
      <>
        <Table.Row key={idx} className=‘tableRow’>
          <Table.Cell className={style.checkbox}>
            <Checkbox
              aria-label={formatMessage({
                id: ‘charges_credits_table_select_rows’,
                defaultMessage: ‘Select rows’,
              })}
              style={{ cursor: ‘pointer’ }}
              checked={isCheckedWhenPaymentAmount}
              onClick={() => handleCheckboxClicked(item.txnId)}
              title=‘Toggle Row Selected’
            />
          </Table.Cell>
          <Table.Cell className={styles.leftAlign}>
            <LinkButton
              onClick={handleNavigate}
              text={`${item.txnType.txnTypeLongName} ${referenceNumber}`}
            />
            {‘ ‘}({toLocalDateFormat(item.date)})
          </Table.Cell>
          <Table.Cell className={styles.leftAlign}>
            {toLocalDateFormat(item.dueDate)}
          </Table.Cell>
          <Table.Cell className={styles.rightAlign}>
            {toLocalizedMoneyString(item.amount)}
          </Table.Cell>
          <Table.Cell className={styles.rightAlign}>
            {toLocalizedMoneyString(item.openBalance)}
          </Table.Cell>
          <Table.Cell>
            <TextField
              autoComplete=‘off’
              value={paymentAmount}
              aria-label={formatMessage({
                id: ‘charges_credits_payment_field’,
                defaultMessage: ‘Payments’,
              })}
              className={styles.paymentField}
              onChange={handlePaymentFieldChange}
              onBlur={handlePaymentOnBlur}
            />
          </Table.Cell>
        </Table.Row>
      </>
    );
  };

  return (
    <div className={styles.gridWrapper}>
      {customerId && (
        <ChargesTable header={<TransactionsTableHeader dueDateFlag />}>
          {(item: OutstandingTableDataTypes, idx: number) => (
            <Items item={item} idx={idx} />
          )}
        </ChargesTable>
      )}
    </div>
  );
};

export default enabled(
  { ‘environment.txnType’: [TransactionTypes.PURCHASE_BILL_PAYMENT] },
  OutstandingTransactionsTable
);
