Creating a simple Golang Gio UI app for listening to Quran with Oto Player requires you to download the appropriate audio files or utilize an API. This example will use the API provided by Quran.com.

First, install both Gio and Oto library:
```sh
go get gioui.org/cmd/gogio
go get github.com/hajimehoshi/oto
```

Then, create a `main.go` file in your project directory and add the following code:

```go
package main

import (
	"encoding/json"
	"fmt"
	"image/color"
	"io/ioutil"
	"log"
	"net/http"
	"strconv"

	"github.com/hajimehoshi/oto"
	"github.com/tosone/minimp3"

	"gioui.org/app"
	"gioui.org/font/gofont"
	"gioui.org/io/key"
	"gioui.org/io/system"
	"gioui.org/layout"
	"gioui.org/unit"
	"gioui.org/widget"
	"gioui.org/widget/material"
)

var (
	apiURL       = "https://api.quran.com/api/v4/quran/recitations/7"
	streamingURL = "http://server-eu.imuslimz.com:8020"
)

type AudioDetail struct {
	ID      int    `json:"id"`
	SurahID int    `json:"surah_id"`
	URL     string `json:"url"`
}

func main() {
	go func() {
		w := app.NewWindow()
		if err := loop(w); err != nil {
			log.Fatal(err)
		}
	}()
	app.Main()
}

func loop(w *app.Window) error {
	th := material.NewTheme(gofont.Collection())
	list := &layout.List{
		Axis: layout.Vertical,
	}
	var audioDetails []AudioDetail

	err := fetchAudioDetails(&audioDetails)
	if err != nil {
		return err
	}

	btns := make([]widget.Clickable, len(audioDetails))
	items := make([]layout.FlexChild, len(audioDetails))

	for {
		e := <-w.Events()
		switch e := e.(type) {
		case system.DestroyEvent:
			return e.Err
		case system.FrameEvent:
			gtx := layout.NewContext(&e.Queue, e.Config, e.Size)

			if k := gtx.Key(); k != nil && k.Name == key.NameEscape {
				os.Exit(0)
			}

			layout.Flex{
				Axis:      layout.Vertical,
				Spacing:   layout.SpaceEvenly,
				Alignment: layout.Middle,
			}.Layout(gtx, func() {
				for i := range audioDetails {
					index := i + 1
					btn := &btns[i]
					item := material.Button(th, btn, fmt.Sprintf("Surah %d", index))
					item.Background = color.RGBA{A: 0xff}
					if btn.Clicked() {
						playAudio(audioDetails[i].URL)
					}
					items[i] = layout.FlexChild{
						Weight: 1,
						Widget: item.Layout,
					}
				}
				list.Layout(gtx, len(items), func(gtx layout.Context, i int) layout.Dimensions {
					gtx.Constraints.Min.X = gtx.Constraints.Max.X / 4
					return items[i].Layout(gtx)
				})
			})

			e.Frame(gtx.Ops)
		}
	}
}

func fetchAudioDetails(audioDetails *[]AudioDetail) error {
	response, err := http.Get(apiURL)
	if err != nil {
		return err
	}
	defer response.Body.Close()

	data, err := ioutil.ReadAll(response.Body)
	if err != nil {
		return err
	}

	err = json.Unmarshal(data, &audioDetails)
	if err != nil {
		return err
	}

	for i := range *audioDetails {
		(*audioDetails)[i].URL = streamingURL + (*audioDetails)[i].URL
	}

	return nil
}

func playAudio(url string) {
	dec, ch, sr, err := getAudioDecoder(url)
	if err != nil {
		log.Println("Error obtaining decoder:", err)
		return
	}

	ctx, err := oto.NewContext(sr, ch, 2, 4096)
	if err != nil {
		log.Println("Error creating oto context:", err)
		return
	}
	defer ctx.Close()

	player := ctx.NewPlayer()
	defer player.Close()

	log.Println("Start playing audio:", url)
	for {
		frame, err := dec.Decode()
		if err != nil {
			break
		}
		player.Write(frame)
	}
	log.Println("Stop playing audio:", url)
}

func getAudioDecoder(url string) (*minimp3.Decoder, int, int, error) {
	resp, err := http.Get(url)
	if err != nil {
		return nil, 0, 0, err
	}

	data, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, 0, 0, err
	}

	dec, ch, sr, err := minimp3.DecodeFull(data)
	if err != nil {
		return nil, 0, 0, err
	}

	return dec, ch, sr, nil
}
```

