#include "AACDecoder/libhelixaac/include/helix_fixed.h"
#include "AACDecoder/libhelixaac/include/aacdec.h"
#include "AACDecoder/libhelixaac/include/sbrdec.h"
#include "AACDecoder/libhelixaac/include/user_interpret.h"
#include "AACDecoder/libhelixaac/include/version.h"

const char* MY_URL = "<your m4a dash audio file url>";
Audio::Buffer* buff = NULL;
HTTPClient httpClient;

uint8_t* internal_block = NULL;

#define INTERNAL_BLOCK_SIZE (1024 * 1024)

void setup() {
  Serial.begin(115200);
  WiFi.begin(WIFI_SSID, WIFI_PASSWORD);
  while (WiFi.status() != WL_CONNECTED) {
    delay(500);
    Serial.print(".");
  }
  Serial.println("Connected");
  pinMode(LED_BUILTIN, OUTPUT);

  httpClient.begin(MY_URL);
  httpClient.setTimeout(2000);
  int fileSize = 0;
  if (httpClient.getSize() > 0) {
    fileSize = httpClient.getSize();
  } else {
    Serial.println("File size error");
    while (true) {
      digitalWrite(LED_BUILTIN, !digitalRead(LED_BUILTIN));
      delay(100);
    }
  }
  internal_block = (uint8_t*) malloc(INTERNAL_BLOCK_SIZE);
  httpClient.getStream().readBytes(internal_block, INTERNAL_BLOCK_SIZE);
  Serial.println("Received internal_block");

  HX_RESULT hResult;
  HAACDecoder hAACDecoder = AACInitDecoder();
  hResult = AACDecode(hAACDecoder, &buff, internal_block, fileSize);

  if (hResult != HXR_OK) {
    Serial.println("Helix AAC decoder failed");
    while (true) {
      digitalWrite(LED_BUILTIN, !digitalRead(LED_BUILTIN));
      delay(100);
    }
  } else {
    Serial.println("Helix AAC decoder succeeded");
  }
}

void loop() {
  digitalWrite(LED_BUILTIN, !digitalRead(LED_BUILTIN));
  delay(1000);
}
