};

uint8_t rowPins[rows] = {0, 1, 2};
uint8_t colPins[cols] = {3, 4, 5};

Keypad_I2C keypad(makeKeymap(keys), rowPins, colPins, rows, cols, ADDR, &Wire);

#define SD_CS 4
#define VS1053_CS 6
#define VS1053_DCS 7
#define VS1053_DREQ 3

Adafruit_VS1053_FilePlayer musicPlayer = Adafruit_VS1053_FilePlayer(SHIELD_VS1053_RESET, SHIELD_VS1053_CS, SHIELD_VS1053_DCS, SHIELD_VS1053_DREQ, SHIELD_VS_LOWPOWER, SHIELD_VS_XCS);

void setup() {
  pinMode(SD_CS, OUTPUT);
  digitalWrite(SD_CS, HIGH);

  Serial.begin(9600);
  keypad.begin();
  keypad.setDebounceTime(10);

  if (!musicPlayer.begin()) {
    Serial.println(F("Music maker not found"));
    while (1);
  }

  if (!SD.begin(SD_CS)) {
    Serial.println(F("SD failed"));
    while (1);
  }

  musicPlayer.setVolume(20, 20);
  Serial.println(F("Adafruit VS1053 Simple Test"));
}

void loop() {
  char button = keypad.getKey();
  if (button) {
    musicPlayer.stopPlaying();
    String filename = "TRACK0";
    filename += button;
    filename += ".WAV";
    startPlaying(filename);
    Serial.print("Now playing: ");
    Serial.println(filename);
  }

  digitalWrite(SD_CS, HIGH);
  delay(100);
}

void startPlaying(String filename) {
  char trackName[10];
  filename.toCharArray(trackName, sizeof(trackName));
  musicPlayer.startPlayingFile(trackName);
  while (musicPlayer.playingMusic);
}
```

This example assumes that you have the following naming for your audio files on the SD card: TRACK01.WAV, TRACK02.WAV, ... TRACK09.WAV.

Below is the updated example Arduino program using the Adafruit Keypad library instead of the Keypad_I2C library:

```cpp
#include <SPI.h>
#include <Adafruit_VS1053.h>
#include <SD.h>
#include <Adafruit_Keypad.h>

const uint8_t rows = 3;
const uint8_t cols = 3;

char keys[rows][cols] = {
  {'1', '2', '3'},
  {'4', '5', '6'},
  {'7', '8', '9'}
};

uint8_t rowPins[rows] = {9, 10, 11}; // connect keypad ROW0, ROW1, ROW2
uint8_t colPins[cols] = {6, 7, 8};   // connect keypad COL0, COL1, COL2

Adafruit_Keypad keypad = Adafruit_Keypad(makeKeymap(keys), rowPins, colPins, rows, cols);

#define SD_CS 4
#define VS1053_CS 6
#define VS1053_DCS 7
#define VS1053_DREQ 3

Adafruit_VS1053_FilePlayer musicPlayer = Adafruit_VS1053_FilePlayer(SHIELD_VS1053_RESET, SHIELD_VS1053_CS, SHIELD_VS1053_DCS, SHIELD_VS1053_DREQ, SHIELD_VS_LOWPOWER, SHIELD_VS_XCS);

void setup() {
  pinMode(SD_CS, OUTPUT);
  digitalWrite(SD_CS, HIGH);

  Serial.begin(9600);

  if (!musicPlayer.begin()) {
    Serial.println(F("Music maker not found"));
    while (1);
  }

  if (!SD.begin(SD_CS)) {
    Serial.println(F("SD failed"));
    while (1);
  }

  musicPlayer.setVolume(20, 20);
  Serial.println(F("Adafruit VS1053 Simple Test"));
}

void loop() {
  KeyEvent atomic_key = keypad.read();  // read the key state

  if (atomic_key.bit.EVENT == KEY_JUST_PRESSED) {  // check whether a key has been pressed
    musicPlayer.stopPlaying();             // stop playing previous audio
    char button = atomic_key.bit.KEY;
    String filename = "TRACK0";
    filename += button;
    filename += ".WAV";
    startPlaying(filename);
    Serial.print("Now playing: ");
    Serial.println(filename);

    digitalWrite(SD_CS, HIGH);
  }

  delay(100);
}

void startPlaying(String filename) {
  char trackName[10];
  filename.toCharArray(trackName, sizeof(trackName));
  musicPlayer.startPlayingFile(trackName);
  while (musicPlayer.playingMusic);
}
```

This example assumes that you have the following naming for your audio files on the SD card: TRACK01.WAV, TRACK02.WAV, ... TRACK09.WAV. Make sure to connect the keypad pins to the appropriate Arduino pins as specified in rowPins and colPins arrays.

В WPF для воспроизведения mp3 файлов можно использовать класс MediaPlayer из пространства имен System.Windows.Media. 

Для начала необходимо добавить требуемый mp3 файл в проект WPF с помощью контекстного меню "Add Existing Item" в Solution Explorer. 

Затем в коде создаем экземпляр класса MediaPlayer и передаем ему путь к mp3 файлу:

```
MediaPlayer mediaPlayer = new MediaPlayer();
mediaPlayer.Open(new Uri(@"путь_к_файлу.mp3"));
```

Далее можно использовать методы Play() и Pause() для управления воспроизведением:

```
mediaPlayer.Play(); // начать воспроизведение
mediaPlayer.Pause(); // приостановить воспроизведение
```

Также можно задать дополнительные параметры воспроизведения, например, установить громкость:

```
mediaPlayer.Volume = 0.5; // установить громкость на 50%
```

Полный пример кода:

```
using System;
using System.Windows;
using System.Windows.Media;

namespace WpfApp1
{
    public partial class MainWindow : Window
    {
        public MainWindow()
        {
            InitializeComponent();

            MediaPlayer mediaPlayer = new MediaPlayer();
            mediaPlayer.Open(new Uri(@"путь_к_файлу.mp3"));

            mediaPlayer.Play();
        }
    }
}
```

Возможно, причиной проблемы может быть недостаточное время буферизации аудио данных, что может привести к остановке воспроизведения через несколько секунд.

Для решения этой проблемы можно увеличить время буферизации аудио данных, чтобы дать MediaPlayer больше времени на загрузку и кэширование аудио-файла. Для этого необходимо использовать свойство MediaOpened класса MediaPlayer и установить желаемое значение в миллисекундах с помощью метода SetPropertyValue класса MediaBase:

```
MediaPlayer mediaPlayer = new MediaPlayer();
mediaPlayer.MediaOpened += mediaPlayer_MediaOpened;

private void mediaPlayer_MediaOpened(object sender, EventArgs e)
{
    MediaPlayer mediaPlayer = sender as MediaPlayer;
    mediaPlayer?.SetPropertyValue(MediaPlayer.BufferingTimeProperty, new TimeSpan(0, 0, 10));
}
```

В данном примере мы устанавливаем время буферизации в 10 секунд. 

Также может помочь использование асинхронного метода PlayAsync(), который дает MediaPlayer больше времени на загрузку и буферизацию аудио данных:

```
MediaElement mediaElement = new MediaElement();
mediaElement.Source = new Uri("путь_к_аудио_файлу.mp3", UriKind.Relative);
mediaElement.LoadedBehavior = MediaState.Manual;

await mediaElement.PlayAsync();
```

Пример выше показывает использование MediaElement вместо MediaPlayer, но идея применима в обоих случаях.

К сожалению, в классе MediaPlayer нет метода PlayAsync(), так как этот метод является расширением для класса MediaElement. 

Таким образом, чтобы воспроизводить аудио-файлы асинхронно в WPF с помощью MediaElement, необходимо использовать метод Play() в отдельном потоке, например, в Task или в потоке ThreadPool:

```
MediaElement mediaElement = new MediaElement();
mediaElement.Source = new Uri("путь_к_аудио_файлу.mp3", UriKind.Relative);
mediaElement.LoadedBehavior = MediaState.Manual;

await Task.Run(() =>
{
    mediaElement.Dispatcher.Invoke(() =>
    {
        mediaElement.Play();
    });
});
```

В данном примере мы используем метод Dispatcher.Invoke(), чтобы выполнить метод Play() в главном UI-потоке, так как не всегда удается выполнить методы MediaElement в других потоках напрямую.

Кроме того, не забудьте установить LoadedBehavior в значение Manual, чтобы MediaElement не начинал воспроизведение автоматически при загрузке страницы.

К сожалению, MediaElement не имеет метода PlayAsync() в стандартной реализации WPF, но вы можете создать расширение метода Play(), чтобы воспроизводить аудио-файлы асинхронно.

Для этого создайте статический класс, содержащий метод расширения для MediaElement:

```
public static class MediaElementExtensions
{
    public static Task PlayAsync(this MediaElement mediaElement)
    {
        mediaElement.Play();
        return Task.Delay((int)mediaElement.NaturalDuration.TimeSpan.TotalMilliseconds);
    }
}
```

В этом примере метод PlayAsync() просто вызывает метод Play(), а затем возвращает задержку, равную длительности аудио-файла. После вызова этого метода можно использовать ключевое слово await, чтобы продолжить работу, когда воспроизведение закончится:

```
MediaElement mediaElement = new MediaElement();
mediaElement.Source = new Uri("путь_к_аудио_файлу.mp3", UriKind.Relative);
mediaElement.LoadedBehavior = MediaState.Manual;

await mediaElement.PlayAsync();

// продолжение работы после воспроизведения
```

Заметьте, что когда проигрывание файла закончится, выполнение программы продолжится с новой строки после await. 

Однако здесь также может возникнуть проблема с использованием метода MediaElement в других потоках, например, вы можете получить ошибку, связанную с работой с UI-элементами в другом потоке. Чтобы избежать этого, следует вызывать метод Play() в главном UI-потоке, используя метод Dispatcher.Invoke():

```
await mediaElement.Dispatcher.InvokeAsync(() => mediaElement.Play());
```

Теперь вы можете вызывать метод PlayAsync() асинхронно и получать уведомление, когда воспроизведение закончится.

Для взаимодействия с элементом progressbar в WPF можно использовать свойство Value, которое задает текущее значение прогресса. Пример:

XAML код:
```xml
<ProgressBar Width="200" Height="20" Minimum="0" Maximum="100" Value="{Binding ProgressValue}" />
```

C# код:
```csharp
public partial class MainWindow : Window, INotifyPropertyChanged
{
    private int progressValue;

    public int ProgressValue
    {
        get { return progressValue; }
        set 
        { 
            progressValue = value;
            OnPropertyChanged("ProgressValue");
        }
    }

    public MainWindow()
    {
        InitializeComponent();
        DataContext = this;

        // Пример изменения прогресса
        Task.Run(() =>
        {
            for (int i = 0; i <= 100; i++)
            {
                ProgressValue = i;
                Thread.Sleep(100);
            }
        });
    }

    // Реализация интерфейса INotifyPropertyChanged
    public event PropertyChangedEventHandler PropertyChanged;

    protected void OnPropertyChanged([CallerMemberName] string propertyName = "")
    {
        PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(propertyName));
    }
}
```

В данном примере прогрессбар связывается с свойством ProgressValue через привязку данных (Data Binding). При изменении значения свойства ProgressValue, прогрессбар автоматически обновляет значение своего прогресса. 

В примере происходит изменение значения ProgressValue в отдельном потоке с использованием класса Task и задержки Thread.Sleep. В реальной программе могут быть другие способы изменения прогресса в зависимости от логики программы.

Для получения длительности аудиофайла, воспроизводимого с помощью элемента управления MediaPlayer в WPF, нужно использовать свойство NaturalDuration.Duration элемента MediaPlayer.

Пример:

```csharp
MediaPlayer mediaPlayer = new MediaPlayer();

// загружаем аудиофайл
mediaPlayer.Open(new Uri(@"C:\Music\example.mp3"));

// получаем длительность аудиофайла
TimeSpan duration = mediaPlayer.NaturalDuration.TimeSpan;

// выводим длительность в формате минуты:секунды
string durationStr = $"{duration.Minutes}:{duration.Seconds}";
Console.WriteLine(durationStr);
```

В этом примере мы создали экземпляр MediaPlayer, загрузили аудиофайл и получили его длительность с помощью свойства NaturalDuration.Duration. Затем длительность преобразовали в формат ММ:СС и вывели в консоль. 

Обратите внимание, что для корректной работы свойства NaturalDuration.Duration, необходимо дождаться полной загрузки аудиофайла, перед тем как получать его длительность.

Для создания элементов внутри других элементов в WPF можно использовать методы Add() и AddRange() соответственно свойств List<UIElement> и Children для контейнерных элементов, таких, как Panel или Canvas.

Пример создания кнопки внутри StackPanel:

```csharp
StackPanel stackPanel = new StackPanel();

Button button = new Button();
button.Content = "Click me!";

stackPanel.Children.Add(button);
```

В данном примере создаем экземпляр StackPanel и экземпляр Button, задаем содержимое кнопки и добавляем кнопку в список дочерних элементов StackPanel.

Пример создания нескольких кнопок и добавление их в ListBox:

```csharp
ListBox listBox = new ListBox();

Button button1 = new Button();
button1.Content = "Button 1";

Button button2 = new Button();
button2.Content = "Button 2";

Button button3 = new Button();
button3.Content = "Button 3";

listBox.Items.Add(button1);
listBox.Items.Add(button2);
listBox.Items.Add(button3);
```

В данном примере создаем экземпляры Button, задаем их содержимое и добавляем в список элементов ListBox с помощью метода Items.Add().

Обратите внимание, что в случае использования метода AddRange(), передаем список элементов в качестве аргумента.

```csharp
StackPanel stackPanel = new StackPanel();

Button button1 = new Button();
button1.Content = "Button 1";

Button button2 = new Button();
button2.Content = "Button 2";

Button button3 = new Button();
button3.Content = "Button 3";

stackPanel.Children.AddRange(new List<UIElement> { button1, button2, button3 });
```

В этом примере создаем экземпляры Button, задаем их содержимое и добавляем в список дочерних элементов StackPanel с помощью метода Children.AddRange().

Для получения обложки аудиофайла, воспроизводимого с помощью элемента управления MediaPlayer в WPF, можно использовать класс TagLib#.

TagLib# - это библиотека для чтения и записи метаданных в различных аудиофайлах, таких, как MP3, FLAC, Ogg Vorbis и др. С помощью TagLib# можно получать информацию о файле, такую как название, исполнитель, альбом и обложку.

Пример:

```csharp
MediaPlayer mediaPlayer = new MediaPlayer();

// загружаем аудиофайл
mediaPlayer.Open(new Uri(@"C:\Music\example.mp3"));

// получаем путь к файлу
string filePath = @"C:\Music\example.mp3";

// загружаем метаданные из файла
TagLib.File file = TagLib.File.Create(filePath);

// получаем обложку
byte[] imageData = file.Tag.Pictures[0].Data.Data;
MemoryStream ms = new MemoryStream(imageData);

// создаем изображение
BitmapImage image = new BitmapImage();
image.BeginInit();
image.StreamSource = ms;
image.CacheOption = BitmapCacheOption.OnLoad;
image.EndInit();

// присваиваем изображение элементу управления Image
ImageCover.Source = image;
```

В этом примере создаем экземпляр MediaPlayer, загружаем аудиофайл и получаем путь к нему. Затем загружаем метаданные из файла с помощью библиотеки TagLib# и получаем обложку из метаданных. 

Далее создаем изображение с помощью класса BitmapImage и присваиваем его элементу управления Image на окне.

Обратите внимание, что в данном примере предполагается, что обложка находится в первом элементе массива Pictures в метаданных файла. Если метаданные не содержат обложку или ее расположение не соответствует первому элементу массива Pictures, будет выброшено исключение.
