% 中心削波法
function y = center_clipping(x, alpha)
    y = zeros(size(x));
    for i = 2:length(x)-1
        if abs(x(i)) > alpha/2
            y(i) = sign(x(i))*(abs(x(i))-alpha/2);
        else
            y(i) = x(i);
        end
    end
end

% 三电平削波法
function y = three_level_clipping(x, alpha)
    y = zeros(size(x));
    for i = 1:length(x)
        if x(i) > alpha/2
            y(i) = alpha/2;
        elseif x(i) < -alpha/2
            y(i) = -alpha/2;
        else
            y(i) = x(i);
        end
    end
end

