以下是一个可能的实现：

```matlab
function pitch_detection()
    % 读取音频文件
    [x, Fs] = audioread('suojinxin_2017131414.wav');
    x = x(:, 1); % 只选取一个声道

    % 切成帧
    frame_size = 25; % 帧长25ms
    frame_shift = 10; % 帧移10ms
    frame_len = fix(frame_size/1000*Fs);
    frame_inc = fix(frame_shift/1000*Fs);
    w = hamming(frame_len);

    frames = buffer(x, frame_len, frame_len-frame_inc);
    frames = frames .* repmat(w, 1, size(frames, 2));

    % 计算每个帧的自相关函数
    frames_zero = zeros(size(frames));
    frames_peak = zeros(size(frames));

    for i = 1:size(frames, 2)
        R = xcorr(frames(:, i));
        R = R(frame_len:end); % 只取正半轴
        R_zero = zero_clipping(R);
        R_peak = three_level_clipping(R);

        % 找到峰值
        [pks, locs] = findpeaks(R);
        [~, idx] = max(pks);
        peak_loc = locs(idx);

        [z_pks, z_locs] = findpeaks(R_zero);
        [~, z_idx] = max(z_pks);
        zero_loc = z_locs(z_idx);

        [p_pks, p_locs] = findpeaks(R_peak);
        [~, p_idx] = max(p_pks);
        peak_loc = p_locs(p_idx);

        frames_zero(zero_loc, i) = 1;
        frames_peak(peak_loc, i) = 1;
    end

    % 将周期转化为频率
    freq_zero = Fs ./ period_to_time(find(frames_zero));
    freq_peak = Fs ./ period_to_time(find(frames_peak));

    % 绘图
    x_len = length(x);
    t = (0:x_len-1) / Fs;
    figure();
    subplot(3, 1, 1); plot(t, x); hold on;
    plot(find(frames_zero(:))/Fs, frames_zero(:)*max(x), 'g.');
    title('原始音频信号和中心削波效果');
    subplot(3, 1, 2); plot(t, x); hold on;
    plot(find(frames_peak(:))/Fs, frames_peak(:)*max(x), 'r.');
    title('原始音频信号和三电平削波效果');
    subplot(3, 1, 3); plot(t, freq_zero); hold on;
    plot(t, freq_peak, 'r');
    title('中心削波和三电平削波的基音频率');
end

% 中心削波
function R_zero = zero_clipping(R)
    R_zero = zeros(size(R));
    center = fix(length(R)/2);
    R_zero(center) = max(R);
end

% 三电平削波
function R_peak = three_level_clipping(R)
    R_peak = zeros(size(R));
    R_max = max(R);
    a = 0.35;
    b = 0.7;
    seg1 = R(1:fix(a*length(R)));
    [~, seg1_max] = max(seg1);
    R_peak(seg1_max) = seg1(seg1_max);
    seg2 = R(fix(a*length(R)):fix(b*length(R)));
    [~, seg2_max] = max(seg2);
    R_peak(fix(a*length(R))+seg2_max-1) = seg2(seg2_max);
    seg3 = R(fix(b*length(R)):end);
    [~, seg3_max] = max(seg3);
    R_peak(fix(b*length(R))+seg3_max-1) = seg3(seg3_max);
    R_peak = R_peak * R_max;
end

% 周期转化为时间
function t = period_to_time(p)
    t = p / (44100/10); % 帧移10ms，采样频率44100
end
```

