import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.net.HttpURLConnection;
import java.net.URL;
import java.net.URLEncoder;

public class ChatGPTClient {

    public static void main(String[] args) {
        String apiKey = "API_KEY_HERE"; // 请替换为您的API Key
        String message = "Hello!"; // 要发送的消息

        try {
            // 创建URL对象并设置连接属性
            URL url = new URL("https://api.openai.com/v1/engines/davinci-codex/completions");
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("POST");
            connection.setRequestProperty("Content-Type", "application/json");
            connection.setRequestProperty("Authorization", "Bearer " + apiKey);
            connection.setDoOutput(true);

            // 准备请求数据
            String requestData = "{\"prompt\":\"" + message + "\",\"max_tokens\":100,\"temperature\":0.5,\"n\":1,\"stop\":null}";

            // 发送请求
            OutputStreamWriter writer = new OutputStreamWriter(connection.getOutputStream());
            writer.write(requestData);
            writer.flush();

            // 读取响应数据
            BufferedReader reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));
            StringBuilder response = new StringBuilder();
            String line;
            while ((line = reader.readLine()) != null) {
                response.append(line);
            }
            reader.close();

            // 输出响应数据
            System.out.println(response.toString());

        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
```

