    public async Task<string> GetAsync(string apiUrl)
    {
        HttpResponseMessage response = await _httpClient.GetAsync(apiUrl);

        if (response.IsSuccessStatusCode)
        {
            return await response.Content.ReadAsStringAsync();
        }
        else
        {
            throw new Exception($"Api request failed with status code: {response.StatusCode}");
        }
    }

    public async Task<string> PostAsync<T>(string apiUrl, T data)
    {
        string dataJson = System.Text.Json.JsonSerializer.Serialize(data);
        HttpResponseMessage response = await _httpClient.PostAsync(apiUrl, new StringContent(dataJson));

        if (response.IsSuccessStatusCode)
        {
            return await response.Content.ReadAsStringAsync();
        }
        else
        {
            throw new Exception($"Api request failed with status code: {response.StatusCode}");
        }
    }

    public async Task<string> PutAsync<T>(string apiUrl, T data)
    {
        string dataJson = System.Text.Json.JsonSerializer.Serialize(data);
        HttpResponseMessage response = await _httpClient.PutAsync(apiUrl, new StringContent(dataJson));

        if (response.IsSuccessStatusCode)
        {
            return await response.Content.ReadAsStringAsync();
        }
        else
        {
            throw new Exception($"Api request failed with status code: {response.StatusCode}");
        }
    }

    public async Task DeleteAsync(string apiUrl)
    {
        HttpResponseMessage response = await _httpClient.DeleteAsync(apiUrl);

        if (!response.IsSuccessStatusCode)
        {
            throw new Exception($"Api request failed with status code: {response.StatusCode}");
        }
    }
}
