import 'dart:html' as html;

void splitImage(html.ImageElement image) {
  html.CanvasElement canvas = html.CanvasElement(width: image.width, height: image.height);
  html.CanvasRenderingContext2D ctx = canvas.context2D;

  // Draw the top-left part of the image
  ctx.drawImageScaled(image, 0, 0, image.width / 2, image.height / 2, 0, 0, image.width / 2, image.height / 2);

  // Draw the top-right part of the image
  ctx.drawImageScaled(image, image.width / 2, 0, image.width / 2, image.height / 2, image.width / 2, 0, image.width / 2, image.height / 2);

  // Draw the bottom-left part of the image
  ctx.drawImageScaled(image, 0, image.height / 2, image.width / 2, image.height / 2, 0, image.height / 2, image.width / 2, image.height / 2);

  // Draw the bottom-right part of the image
  ctx.drawImageScaled(image, image.width / 2, image.height / 2, image.width / 2, image.height / 2, image.width / 2, image.height / 2, image.width / 2, image.height / 2);

