import java.sql.*;

public class ThesaurusQuery {
    private static final String DB_URL = "jdbc:mysql://localhost/thesaurus";
    private static final String DB_USER = "root";
    private static final String DB_PASSWORD = "";

    public static void main(String[] args) {
        try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {
            String word = getUserInput();
            boolean isInThesaurus = isWordInThesaurus(conn, word);
            if (isInThesaurus) {
                System.out.println(word + " is in the thesaurus.");
            } else {
                System.out.println(word + " is not in the thesaurus.");
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
    }

    private static String getUserInput() {
        // Use a Scanner or other input method to get the user's input word
        return "example";
    }

    private static boolean isWordInThesaurus(Connection conn, String word) throws SQLException {
        String sql = "SELECT COUNT(*) FROM entries WHERE word = ?";
        try (PreparedStatement stmt = conn.prepareStatement(sql)) {
            stmt.setString(1, word);
            ResultSet rs = stmt.executeQuery();
            rs.next();
            return rs.getInt(1) > 0;
        }
    }
}
```

