  console.error("Database error:", event.target.errorCode);
};
request.onupgradeneeded = event => {
  const db = event.target.result;
  const store = db.createObjectStore(DB_STORE, { keyPath: 'id', autoIncrement: true });
  store.createIndex(WORD_INDEX, 'word', { unique: true });
};
request.onsuccess = event => {
  const db = event.target.result;
  const word = prompt("Enter a word to look up:");
  if (word) {
    const tx = db.transaction(DB_STORE, 'readonly');
    const store = tx.objectStore(DB_STORE);
    const index = store.index(WORD_INDEX);
    const request = index.count(word);
    request.onsuccess = event => {
      const count = event.target.result;
      console.log(`${word} ${count > 0 ? "is" : "is not"} in the thesaurus.`)
    };
    request.onerror = event => {
