@PostMapping("/sentiment")
public String sentiment(@RequestBody String input) throws IOException {
  LanguageServiceClient languageServiceClient = getLanguageServiceClient();
  Document document = Document.newBuilder().setContent(input).setType(Document.Type.PLAIN_TEXT).build();
  Sentiment sentiment = languageServiceClient.analyzeSentiment(document).getDocumentSentiment();
