9. Create a new controller class that handles the page requests for the CMS. Add actions for creating, editing, and deleting pages.

```
public class PagesController : Controller
{
    private readonly CMSDbContext _context;

    public PagesController(CMSDbContext context)
    {
        _context = context;
    }

    public IActionResult Create()
    {
        var pageTypes = _context.PageTypes.ToList();
        ViewBag.PageTypes = new SelectList(pageTypes, "Id", "Name");
        return View();
    }

    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Create([Bind("Title,Url,PageTypeId,TemplateId")] Page page)
    {
        if (ModelState.IsValid)
        {
            _context.Add(page);
            await _context.SaveChangesAsync();
            return RedirectToAction(nameof(Index));
        }

        var pageTypes = _context.PageTypes.ToList();
        ViewBag.PageTypes = new SelectList(pageTypes, "Id", "Name", page.PageTypeId);
        return View(page);
    }

    public IActionResult Edit(int? id)
    {
        if (id == null)
        {
            return NotFound();
        }

        var page = _context.Pages.Include(p => p.PageType).FirstOrDefault(p => p.Id == id);
        if (page == null)
        {
            return NotFound();
        }

        var pageTypes = _context.PageTypes.ToList();
        ViewBag.PageTypes = new SelectList(pageTypes, "Id", "Name", page.PageTypeId);

        var templateOptions = _context.Templates.Where(t => t.ContentHtml.Contains("%content%"))
            .Select(t => new SelectListItem { Value = t.Id.ToString(), Text = t.Name }).ToList();
        ViewBag.TemplateOptions = templateOptions;
        return View(page);
    }

    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Edit(int id, [Bind("Id,Title,Url,PageTypeId,TemplateId")] Page page)
    {
        if (id != page.Id)
        {
            return NotFound();
        }

        if (ModelState.IsValid)
        {
            try
            {
                _context.Update(page);
                await _context.SaveChangesAsync();
            }
            catch (DbUpdateConcurrencyException)
            {
                if (!PageExists(page.Id))
                {
                    return NotFound();
                }
                else
                {
                    throw;
                }
            }
            return RedirectToAction(nameof(Index));
        }

        var pageTypes = _context.PageTypes.ToList();
        ViewBag.PageTypes = new SelectList(pageTypes, "Id", "Name", page.PageTypeId);

        var templateOptions = _context.Templates.Where(t => t.ContentHtml.Contains("%content%"))
            .Select(t => new SelectListItem { Value = t.Id.ToString(), Text = t.Name }).ToList();
        ViewBag.TemplateOptions = templateOptions;
        return View(page);
    }

