const filePath = '/path/to/file.xlsx';

if (mime.lookup(filePath) === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') {
    console.log('This is an Excel file');
    // 进一步处理 Excel 文件
} else {
    console.log('This is not an Excel file');
