```
using System;

class CaesarCipher
{
    static void Main()
    {
        Console.WriteLine("Введите слово: ");
        string word = Console.ReadLine();

        Console.WriteLine("Введите ключ: ");
        int key = int.Parse(Console.ReadLine());

        string encryptedWord = Encrypt(word, key);
        Console.WriteLine($"Шифр: {encryptedWord}");
        Console.WriteLine($"Было: {word}");

        string decryptedWord = Decrypt(encryptedWord, key);
        Console.WriteLine($"Расшифрованное: {decryptedWord}");
    }

    static string Encrypt(string word, int key)
    {
        string encrypted = "";

        for (int i = 0; i < word.Length; i++)
        {
            char letter = word[i];
            int newLetter = ((letter - 'А') + key) % 32 + 'А'; // находим новый символ по формуле
            encrypted += (char)newLetter;
        }

        return encrypted;
    }

    static string Decrypt(string word, int key)
    {
        string decrypted = "";

        for (int i = 0; i < word.Length; i++)
        {
            char letter = word[i];
            int newLetter = ((letter - 'А') - key + 32) % 32 + 'А'; // обратная формула
            decrypted += (char)newLetter;
        }

        return decrypted;
    }
}
