using System;

class Program {
    static void Main(string[] args) {
        Console.Write("Введите слово: ");
        string word = Console.ReadLine();
        Console.Write("Введите ключ: ");
        int key = int.Parse(Console.ReadLine());

        string cipher = CaesarCipher(word, key);
        Console.WriteLine("Шифр: " + cipher);

        string decoded = CaesarDecipher(cipher, key);
        Console.WriteLine("Было: " + decoded);
    }

    static string CaesarCipher(string word, int key) {
        char[] letters = word.ToCharArray();
        for (int i = 0; i < letters.Length; i++) {
            letters[i] = (char)('А' + ((letters[i] - 'А' + key) % 32));
        }
        return new string(letters);
    }

    static string CaesarDecipher(string cipher, int key) {
        char[] letters = cipher.ToCharArray();
        for (int i = 0; i < letters.Length; i++) {
            letters[i] = (char)('А' + ((letters[i] - 'А' - key + 32) % 32));
        }
        return new string(letters);
    }
}

