```csharp
using System;
using System.Collections.Generic;
using System.Linq;

class Program
{
    static void Main()
    {
        var data = new[]
        {
            "1002143661----aa99999",
            "1011218311----qwe111222",
            "1011926920----dncjk84602",
            "1034910494----a123789",
            "1042533986----qq123456",
        };

        var passwordGroups = data
            .Select(row => row.Split(new[] { "----" }, StringSplitOptions.RemoveEmptyEntries))
            .GroupBy(cols => cols[1])
            .Select(group => new
            {
                Password = group.Key,
                Accounts = group.Select(cols => cols[0])
            });

        foreach (var group in passwordGroups)
        {
            Console.WriteLine($"Password: {group.Password}");
            Console.WriteLine($"Accounts: {string.Join(", ", group.Accounts)}");
        }
    }
}
```

