                    else if (lineNum == 13)
                    {
                        string expiryDate = GetExpiryDate(line);
                        Console.WriteLine("身份证有效期限是：{0}", expiryDate);
                    }
                }
            }

            Console.WriteLine("身份证信息提取完毕。");
        }

        static string GetEthnicity(string input)
        {
            Regex regex = new Regex(@"^民族：([\u4e00-\u9fa5]+)$");
            Match match = regex.Match(input);
            if (match.Success)
            {
                string ethnicity = match.Groups[1].Value;
                char ethnicityChar = ethnicity[ethnicity.Length - 1];
                return ethnicityChar.ToString();
            }
            else
            {
                return "";
            }
        }

        static string GetExpiryDate(string input)
        {
            Regex regex = new Regex(@"^有效期限\s+(\d{4}\.\d{2}\.\d{2})-(\d{4}\.\d{2}\.\d{2})$");
            Match match = regex.Match(input);
            if (match.Success)
            {
                string startDate = match.Groups[1].Value;
                string endDate = match.Groups[2].Value;
                return startDate + "-" + endDate;
            }
            else
            {
                return "";
            }
        }
    }
}
