// Callback function for SQLite queries
static int query_callback(void* data, int argc, char** argv, char** azColName)
{
    int* count = reinterpret_cast<int*>(data);
    for (int i = 0; i < argc; i++)
    {
        std::cout << azColName[i] << " = " << argv[i] << std::endl;
    }
    (*count)++;
    return 0;
}

// Utility function to create SQLite table for file system metadata
void create_table(sqlite3* db)
{
    std::string sql = "CREATE TABLE IF NOT EXISTS filesystem (path TEXT PRIMARY KEY, is_dir INTEGER, size INTEGER)";
    char* errmsg;
    int rc = sqlite3_exec(db, sql.c_str(), nullptr, nullptr, &errmsg);
    if (rc != SQLITE_OK)
    {
        std::cerr << "Error creating table: " << errmsg << std::endl;
        sqlite3_free(errmsg);
    }
}

// Utility function to insert metadata for a file or directory into SQLite table
void insert_metadata(sqlite3* db, const fs::path& path, bool is_dir, int size)
{
    std::string sql = "INSERT INTO filesystem (path, is_dir, size) VALUES ('" + path.string() + "', " + std::to_string(is_dir) + ", " + std::to_string(size) + ")";
    char* errmsg;
    int rc = sqlite3_exec(db, sql.c_str(), nullptr, nullptr, &errmsg);
    if (rc != SQLITE_OK)
    {
        std::cerr << "Error inserting metadata: " << errmsg << std::endl;
        sqlite3_free(errmsg);
    }
}

// Utility function to remove metadata for a file or directory from SQLite table
void remove_metadata(sqlite3* db, const fs::path& path)
{
    std::string sql = "DELETE FROM filesystem WHERE path = '" + path.string() + "'";
    char* errmsg;
    int rc = sqlite3_exec(db, sql.c_str(), nullptr, nullptr, &errmsg);
    if (rc != SQLITE_OK)
    {
        std::cerr << "Error removing metadata: " << errmsg << std::endl;
        sqlite3_free(errmsg);
    }
}

// Utility function to get the size of a file or directory
int get_size(const fs::path& path)
{
    int size = 0;
    if (fs::is_regular_file(path))
    {
        size = fs::file_size(path);
    }
    else if (fs::is_directory(path))
    {
        for (const auto& p : fs::recursive_directory_iterator(path))
        {
            if (fs::is_regular_file(p.path()))
            {
                size += fs::file_size(p);
            }
        }
    }
    return size;
}

