        exit(1);
    } else {
        fprintf(stderr, "Opened database successfully\n");
    }

    // Create table
    char* sql = "CREATE TABLE IF NOT EXISTS metadata (id INTEGER PRIMARY KEY, name TEXT, parentId INTEGER, size INTEGER, isDirectory INTEGER)";
    rc = sqlite3_exec(db, sql, NULL, 0, &errmsg);

    if (rc != SQLITE_OK) {
        fprintf(stderr, "SQL error: %s\n", errmsg);
        sqlite3_free(errmsg);
        exit(1);
    }
