#include "DirectoryMetadata.h"

DirectoryMetadata::DirectoryMetadata() : db(nullptr) {}

DirectoryMetadata::~DirectoryMetadata() {
    closeDatabase();
}

bool DirectoryMetadata::createDatabase(const std::string &dbName) {
    if (sqlite3_open(dbName.c_str(), &db)) {
        std::cerr << "Error opening sqlite database: " << sqlite3_errmsg(db) << std::endl;
        return false;
    }

    std::string createTableSql = "CREATE TABLE IF NOT EXISTS metadata ("
                                 "path TEXT PRIMARY KEY,"
                                 "filename TEXT,"
                                 "size INTEGER);";

    return executeSql(createTableSql);
}

bool DirectoryMetadata::closeDatabase() {
    sqlite3_close(db);
    db = nullptr;
    return true;
}

bool DirectoryMetadata::addFile(const std::string &dir, const std::string &filename, uint64_t size) {
    std::string addFileSql = "INSERT INTO metadata (path, filename, size) VALUES ('" +
                             dir + "','" + filename + "'," + std::to_string(size) + ");";

    return executeSql(addFileSql);
}

bool DirectoryMetadata::removeFile(const std::string &dir, const std::string &filename) {
    std::string removeFileSql = "DELETE FROM metadata WHERE path ='" + dir +
                                "' AND filename = '" + filename + "';";

    return executeSql(removeFileSql);
}

