#include <iostream>
#include <string>
#include <vector>
#include <utility>
#include <stdexcept>
#include <algorithm>
#include <iterator>
#include <sqlite3.h>

using namespace std;

// Database related code

class Db {
public:
    Db(const string& db_path) : db_path_(db_path) {
        int result = sqlite3_open(db_path.c_str(), &db_);
        if (result != SQLITE_OK) {
            throw runtime_error("Failed to open database");
        }
        create_table();
    }

    ~Db() {
        sqlite3_close(db_);
    }

    void create_table() {
        const char* sql = "CREATE TABLE IF NOT EXISTS directory_structure ("
            "id INTEGER PRIMARY KEY AUTOINCREMENT,"
            "name TEXT NOT NULL,"
            "parent_id INTEGER NOT NULL,"
            "level INTEGER NOT NULL,"
            "size INTEGER,"
            "is_file INTEGER NOT NULL,"
            "is_dir INTEGER NOT NULL"
            ");";

        char* error_msg;
        int result = sqlite3_exec(db_, sql, nullptr, 0, &error_msg);
        if (result != SQLITE_OK) {
            throw runtime_error(string("Failed to create table: ") + error_msg);
        }
    }

    void add_file(const string& file_name, int parent_id, int level, int size) {
        add_item(file_name, parent_id, level, size, 1, 0);
    }

    void remove_file(const string& file_name, int parent_id, int level) {
        remove_item(file_name, parent_id, level, 1, 0);
    }

    int add_directory(const string& dir_name, int parent_id, int level) {
        int dir_id = add_item(dir_name, parent_id, level, 0, 0, 1);
        return dir_id;
    }

    void remove_directory(const string& dir_name, int parent_id, int level) {
        remove_item(dir_name, parent_id, level, 0, 1);

        // Also delete all subdirectories and files
        string select_sql = "SELECT id FROM directory_structure WHERE parent_id = ? AND level > ?";
        sqlite3_stmt* select_stmt;
        int result = sqlite3_prepare_v2(db_, select_sql.c_str(), select_sql.length() + 1, &select_stmt, nullptr);
        sqlite3_bind_int(select_stmt, 1, parent_id);
        sqlite3_bind_int(select_stmt, 2, level);
        if (result != SQLITE_OK) {
            throw runtime_error("Failed to prepare select statement");
        }
        while ((result = sqlite3_step(select_stmt)) == SQLITE_ROW) {
            int id = sqlite3_column_int(select_stmt, 0);
            delete_item(id);
        }
        if (result != SQLITE_DONE) {
            throw runtime_error("Failed to execute select statement");
        }
        sqlite3_finalize(select_stmt);
    }

    void recursively(string path) {
        int parent_id = -1;
        int level = 0;
        vector<string> paths = split(path, '/');
        for (const string& item : paths) {
            if (!item.empty()) {
                if (item.find('.') == string::npos) { // directory
                    parent_id = add_or_update_database(item, parent_id, level);
                    level++;
                } else { // file
                    add_file(item, parent_id, level, 10); // set file size to 10 for example
                }
            }
        }
    }

private:
    sqlite3* db_;
    string db_path_;

    void delete_item(int id) {
        string sql = "DELETE FROM directory_structure WHERE id = ?";
        sqlite3_stmt* stmt;
        int result = sqlite3_prepare_v2(db_, sql.c_str(), sql.length() + 1, &stmt, nullptr);
        sqlite3_bind_int(stmt, 1, id);
        if (result != SQLITE_OK) {
            throw runtime_error("Failed to prepare delete statement");
        }
        result = sqlite3_step(stmt);
        if (result != SQLITE_DONE) {
            throw runtime_error("Failed to execute delete statement");
        }
