        sqlite3_bind_text(stmt, 1, name.c_str(), name.length(), nullptr);
        sqlite3_bind_int(stmt, 2, parent_id);
        sqlite3_bind_int(stmt, 3, level);
        sqlite3_bind_int(stmt, 4, size);
        sqlite3_bind_int(stmt, 5, is_file);
        sqlite3_bind_int(stmt, 6, is_dir);
        result = sqlite3_step(stmt);
        if (result != SQLITE_DONE) {
            throw runtime_error("Failed to execute insert statement");
        }
        sqlite3_finalize(stmt);
    }

};

void scan_directory(string path, Db& db, int parent_id = -1, int current_level = 0) {
    // Check if path is valid
    if (path.empty()) return;
    if (path.back() == '\\') path = path.substr(0, path.size()-1);

    WIN32_FIND_DATA find_data;
    HANDLE find_handle = FindFirstFile((path + "\\*.*").c_str(), &find_data);
    if (find_handle == INVALID_HANDLE_VALUE) return;
    do {
        string name = find_data.cFileName;
        if (name == "." || name == "..") continue;
        if (find_data.dwFileAttributes & FILE_ATTRIBUTE_DIRECTORY) {
            // This is a directory
            int dir_id = db.add_directory(name, parent_id, current_level);
            scan_directory(path + "\\" + name, db, dir_id, current_level+1);
        } else {
            // This is a file
            int file_size = find_data.nFileSizeLow;
            db.add_file(name, parent_id, current_level, file_size);
        }
    } while (FindNextFile(find_handle, &find_data) != 0);

    FindClose(find_handle);
}

