int scan_directory(const string &path, sqlite3 *db, int parent_id, int level) {
    static int id = 0;

    try {
        for (const auto &entry : fs::directory_iterator(path)) {
            id++;
            uintmax_t size = 0;
            bool is_dir = false;

            if (entry.is_regular_file()) {
                size = entry.file_size();
