    int64_t total_size = 0;
    sqlite3* db;

    if (sqlite3_open(db_path.c_str(), &db))
    {
        std::cerr << "Cannot open database: " << sqlite3_errmsg(db) << std::endl;
        return -1;
    }

    std::string query_sql = "WITH RECURSIVE subdir(id) AS ("
                            "SELECT id FROM directories WHERE id = ? "
                            "UNION ALL "
                            "SELECT d.id FROM directories AS d JOIN subdir ON d.parent_directory = subdir.id) "
                            "SELECT SUM(size) FROM files WHERE parent_directory IN subdir;";
    sqlite3_stmt* stmt;

    sqlite3_prepare_v2(db, query_sql.c_str(), -1, &stmt, nullptr);
    sqlite3_bind_int(stmt, 1, dir_id);

    if (sqlite3_step(stmt) == SQLITE_ROW)
    {
        total_size = sqlite3_column_int64(stmt, 0);
    }

    sqlite3_finalize(stmt);
    sqlite3_close(db);
    return total_size;
