Next, create a function to monitor changes in the directory structure and update the SQLite database:

```cpp
void monitor_directory(const std::string& db_path, const std::string& dir_path)
{
    std::map<FILETIME, std::string> last_write_map;
    std::string path(dir_path);

    while (true)
    {
        std::filesystem::path monitored_dir{ path };
        store_directory_structure(db_path, monitored_dir, 0); // Store initial structure

        WIN32_FIND_DATA find_data;
        HANDLE find_handle = FindFirstFile((path + "\\*").c_str(), &find_data);

        if (find_handle != INVALID_HANDLE_VALUE)
        {
            do
            {
                last_write_map[find_data.ftLastWriteTime] = find_data.cFileName;
            } 
            while (FindNextFile(find_handle, &find_data));

            FindClose(find_handle);

            while (true) // Run monitoring and update loop
            {
                find_handle = FindFirstFile((path + "\\*").c_str(), &find_data);
                std::string current_name;
                FILETIME current_write_time;

                if (find_handle != INVALID_HANDLE_VALUE)
                {
                    do
                    {
                        current_name = find_data.cFileName;
                        current_write_time = find_data.ftLastWriteTime;

                        auto it = last_write_map.find(current_write_time);

                        if (it == last_write_map.end() || it->second != current_name)
                        {
                            std::cout << "Detected change in: " << current_name << std::endl;
                            last_write_map[current_write_time] = current_name;
                            store_directory_structure(db_path, monitored_dir, 0); // Update structure on change
                        }
                    } 
                    while (FindNextFile(find_handle, &find_data));

                    FindClose(find_handle);
                }

                std::this_thread::sleep_for(std::chrono::seconds(5)); // Monitor every 5 seconds
            }
        }
        else
        {
            std::cout << "Failed to access directory." << std::endl;
            break;
        }
    }
}
