以下是C++实现代码：

首先，需要定义一个文件夹的数据结构：

struct Folder {
    int id; // 文件夹ID
    int parent_id; // 父文件夹ID
    int level; // 文件夹层级
    std::string name; // 文件夹名称
};

然后，定义一个数据库操作类：

#include <sqlite3.h>

class DB {
public:
    DB(const char* db_file_path);
    int create_folder(const Folder& folder);
    int update_folder(const Folder& folder);
    int delete_folder(int folder_id);
    Folder get_folder(int folder_id);
private:
    sqlite3* db;
};

其中，create_folder方法用于添加文件夹；update_folder方法用于更新文件夹；delete_folder方法用于删除文件夹以及其下属文件夹和文件；get_folder方法用于查询指定ID的文件夹信息。

具体实现如下：

DB::DB(const char* db_file_path) {
    int rc = sqlite3_open(db_file_path, &db);
    if(rc != SQLITE_OK) {
        throw std::runtime_error("Can't open database");
    }
    // 创建数据表
    char* err_msg = nullptr;
    rc = sqlite3_exec(db, "CREATE TABLE IF NOT EXISTS folders (id INTEGER PRIMARY KEY, parent_id INTEGER, level INTEGER, name TEXT)", nullptr, nullptr, &err_msg);
    if(rc != SQLITE_OK) {
        throw std::runtime_error(err_msg);
    }
    sqlite3_free(err_msg);
}

int DB::create_folder(const Folder& folder) {
    sqlite3_stmt* stmt = nullptr;
    int rc = sqlite3_prepare_v2(db, "INSERT INTO folders (parent_id, level, name) VALUES (?, ?, ?)", -1, &stmt, nullptr);
    if(rc != SQLITE_OK) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_bind_int(stmt, 1, folder.parent_id);
    sqlite3_bind_int(stmt, 2, folder.level);
    sqlite3_bind_text(stmt, 3, folder.name.c_str(), -1, SQLITE_STATIC);

    rc = sqlite3_step(stmt);
    if(rc != SQLITE_DONE) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_finalize(stmt);
    return sqlite3_last_insert_rowid(db);
}

int DB::update_folder(const Folder& folder) {
    sqlite3_stmt* stmt = nullptr;
    int rc = sqlite3_prepare_v2(db, "UPDATE folders SET parent_id = ?, level = ?, name = ? WHERE id = ?", -1, &stmt, nullptr);
    if(rc != SQLITE_OK) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_bind_int(stmt, 1, folder.parent_id);
    sqlite3_bind_int(stmt, 2, folder.level);
    sqlite3_bind_text(stmt, 3, folder.name.c_str(), -1, SQLITE_STATIC);
    sqlite3_bind_int(stmt, 4, folder.id);

    rc = sqlite3_step(stmt);
    if(rc != SQLITE_DONE) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_finalize(stmt);
    return folder.id;
}

int DB::delete_folder(int folder_id) {
    sqlite3_stmt* stmt = nullptr;
    int rc = sqlite3_prepare_v2(db, "DELETE FROM folders WHERE id = ?", -1, &stmt, nullptr);
    if(rc != SQLITE_OK) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_bind_int(stmt, 1, folder_id);

    rc = sqlite3_step(stmt);
    if(rc != SQLITE_DONE) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_finalize(stmt);

    // 递归删除子文件夹和文件
    rc = sqlite3_prepare_v2(db, "SELECT id FROM folders WHERE parent_id = ?", -1, &stmt, nullptr);
    if(rc != SQLITE_OK) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_bind_int(stmt, 1, folder_id);
    while(sqlite3_step(stmt) == SQLITE_ROW) {
        int child_folder_id = sqlite3_column_int(stmt, 0);
        delete_folder(child_folder_id);
    }
    sqlite3_finalize(stmt);

    rc = sqlite3_prepare_v2(db, "DELETE FROM files WHERE folder_id = ?", -1, &stmt, nullptr);
    if(rc != SQLITE_OK) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_bind_int(stmt, 1, folder_id);

    rc = sqlite3_step(stmt);
    if(rc != SQLITE_DONE) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_finalize(stmt);
    return 0;
}

Folder DB::get_folder(int folder_id) {
    sqlite3_stmt* stmt = nullptr;
    int rc = sqlite3_prepare_v2(db, "SELECT parent_id, level, name FROM folders WHERE id = ?", -1, &stmt, nullptr);
    if(rc != SQLITE_OK) {
        throw std::runtime_error(sqlite3_errmsg(db));
    }
    sqlite3_bind_int(stmt, 1, folder_id);

    rc = sqlite3_step(stmt);
    if(rc != SQLITE_ROW) {
        throw std::runtime_error("Folder not found");
    }

    Folder folder{
        folder_id,
        sqlite3_column_int(stmt, 0),
        sqlite3_column_int(stmt, 1),
        reinterpret_cast<const char*>(sqlite3_column_text(stmt, 2))
    };
    sqlite3_finalize(stmt);
    return folder;
}

