std::vector<FileInfo> getFilesInFolder(int folderID) {
    std::string query = "SELECT * FROM files WHERE folder_id = " + std::to_string(folderID) + ";";
    auto result = executeSelect(query);

    std::vector<FileInfo> files;
    for (const auto& row : result) {
        FileInfo file;
        file.id = row["id"].as<int>();
        file.folderID = row["folder_id"].as<int>();
        file.name = row["name"].as<std::string>();
        file.path = row["path"].as<std::string>();
        file.size = row["size"].as<long>();
        file.type = row["type"].as<std::string>();
        file.createTime = row["create_time"].as<std::string>();
