        parent_id = folder_id;
        level++;
    }

    // 添加文件或文件夹节点
    string insert_sql = "INSERT INTO file_system (name, parent_id, level, is_folder) VALUES ('" + path_str + "', " + to_string(parent_id) + ", " + to_string(level) + ", " + (is_folder ? "1" : "0") + ");";
    rc = sqlite3_exec(db, insert_sql.c_str(), callback, 0, &zErrMsg);
    if (rc != SQLITE_OK) {
        cout << "SQL error: " << zErrMsg << endl;
        sqlite3_free(zErrMsg);
    }

