        cout << "SQL error: " << zErrMsg << endl;
        sqlite3_free(zErrMsg);
        return;
    }

    if (sqlite3_get_table(db, sql.c_str(), NULL, NULL, NULL, NULL, NULL, NULL) == SQLITE_OK) {
        int cnt = sqlite3_data_count(db);
        if (cnt > 0) {
            for (int i = 1; i <= cnt; i++) {
                int id = stoi(sqlite3_column_text(sqlite3_statement, i * 2));
                bool is_folder = stoi(sqlite3_column_text(sqlite3_statement, i * 2 + 1)) == 1;
                if (is_folder) {
                    delete_folder(id);
