```c++
// 添加文件或文件夹
void add_file(const char *path, int parent_id, int level, bool is_folder) {
    sqlite3 *db;
    char *zErrMsg = 0;
    int rc;

    rc = sqlite3_open(DB_FILE, &db);
    if (rc) {
        cout << "Can't open database: " << sqlite3_errmsg(db) << endl;
        exit(1);
    }

    // 分割路径，生成文件夹节点
    string path_str(path);
    size_t pos = path_str.find_first_of("\\/");
    while (pos != string::npos) {
        string folder_name = path_str.substr(0, pos);
        path_str = path_str.substr(pos + 1);
        pos = path_str.find_first_of("\\/");

        // 查询是否已存在同名的文件夹
        string sql = "SELECT id FROM file_system WHERE name=? AND parent_id=?";
        sqlite3_stmt *stmt = NULL;
        sqlite3_prepare_v2(db, sql.c_str(), -1, &stmt, NULL);
        sqlite3_bind_text(stmt, 1, folder_name.c_str(), folder_name.size(), SQLITE_TRANSIENT);
        sqlite3_bind_int(stmt, 2, parent_id);
        rc = sqlite3_step(stmt);
        int folder_id = 0;
        if (rc == SQLITE_ROW) {
            folder_id = sqlite3_column_int(stmt, 0);
        }
        sqlite3_finalize(stmt);

        // 如果不存在，则添加新的文件夹节点
        if (folder_id == 0) {
            string insert_sql = "INSERT INTO file_system (name, parent_id, level, is_folder) VALUES (?, ?, ?, 1);";
            sqlite3_stmt *stmt_insert = NULL;
            sqlite3_prepare_v2(db, insert_sql.c_str(), -1, &stmt_insert, NULL);
            sqlite3_bind_text(stmt_insert, 1, folder_name.c_str(), folder_name.size(), SQLITE_TRANSIENT);
            sqlite3_bind_int(stmt_insert, 2, parent_id);
            sqlite3_bind_int(stmt_insert, 3, level);
            rc = sqlite3_step(stmt_insert);
            sqlite3_finalize(stmt_insert);

            // 获取新添加的节点的ID
            if (rc == SQLITE_DONE) {
                folder_id = sqlite3_last_insert_rowid(db);
            }
        }

        // 更新当前节点的ID和层级
        parent_id = folder_id;
        level++;
    }

    // 添加文件或文件夹节点
    string insert_sql = "INSERT INTO file_system (name, parent_id, level, is_folder) VALUES (?, ?, ?, ?);";
    sqlite3_stmt *stmt_insert = NULL;
    sqlite3_prepare_v2(db, insert_sql.c_str(), -1, &stmt_insert, NULL);
    sqlite3_bind_text(stmt_insert, 1, path_str.c_str(), path_str.size(), SQLITE_TRANSIENT);
    sqlite3_bind_int(stmt_insert, 2, parent_id);
    sqlite3_bind_int(stmt_insert, 3, level);
    sqlite3_bind_int(stmt_insert, 4, is_folder ? 1 : 0);
    rc = sqlite3_step(stmt_insert);
    sqlite3_finalize(stmt_insert);

    sqlite3_close(db);
}

// 删除文件夹及其下所有的文件和文件夹
void delete_folder(int folder_id) {
    sqlite3 *db;
    char *zErrMsg = 0;
    int rc;

    rc = sqlite3_open(DB_FILE, &db);
    if (rc) {
        cout << "Can't open database: " << sqlite3_errmsg(db) << endl;
        exit(1);
    }

    // 递归删除所有子文件夹
    string sql = "SELECT id, is_folder FROM file_system WHERE parent_id=?";
    sqlite3_stmt *stmt = NULL;
    sqlite3_prepare_v2(db, sql.c_str(), -1, &stmt, NULL);
    sqlite3_bind_int(stmt, 1, folder_id);
    while (sqlite3_step(stmt) == SQLITE_ROW) {
        int id = sqlite3_column_int(stmt, 0);
        bool is_folder = sqlite3_column_int(stmt, 1) == 1;
        if (is_folder) {
            delete_folder(id);
        } else {
            // 删除文件
            string delete_sql = "DELETE FROM file_system WHERE id=?;";
            sqlite3_stmt *stmt_delete = NULL;
            sqlite3_prepare_v2(db, delete_sql.c_str(), -1, &stmt_delete, NULL);
            sqlite3_bind_int(stmt_delete, 1, id);
            rc = sqlite3_step(stmt_delete);
            sqlite3_finalize(stmt_delete);
        }
    }
    sqlite3_finalize(stmt);

    // 删除文件夹节点
    string delete_sql = "DELETE FROM file_system WHERE id=? AND is_folder=1;";
    sqlite3_stmt *stmt_delete = NULL;
    sqlite3_prepare_v2(db, delete_sql.c_str(), -1, &stmt_delete, NULL);
    sqlite3_bind_int(stmt_delete, 1, folder_id);
    rc = sqlite3_step(stmt_delete);
    sqlite3_finalize(stmt_delete);

    sqlite3_close(db);
}
```

在以上代码中，我们使用了参数化查询的方式来执行SQL语句，避免了SQL注入和字符串拼接等风险。同时，使用了 `sqlite3_prepare_v2()` 函数来准备执行的SQL语句，然后将参数绑定到语句上，最后使用 `sqlite3_step()` 函数来执行语句并获取结果。在使用完 `sqlite3_get_table()` 函数获取结果集后，我们使用 `sqlite3_free_table()` 函数来释放结果数组。

