        {
            InitializeComponent();

            openFileDialog.Filter = "PNG Изображения (*.png)|*.png|Все файлы(*.*)|*.*";
            saveFileDialog.Filter = "PNG Изображения (*.png)|*.png|Все файлы(*.*)|*.*";
        }

        private void openButton_Click(object sender, EventArgs e)
        {
            if (openFileDialog.ShowDialog() == DialogResult.Cancel)
                return;

            for (int i = 0; i < 10; i++)
            {
                if (workers.Count < 10)
                {
                    BackgroundWorker worker = new BackgroundWorker();
                    worker.WorkerReportsProgress = true;
                    worker.DoWork += Worker_DoWork;
                    worker.ProgressChanged += Worker_ProgressChanged;
                    worker.RunWorkerCompleted += Worker_RunWorkerCompleted;
                    workers.Add(worker);
                }
            }

            int n = 0;

            foreach (BackgroundWorker worker in workers)
            {
                if (worker.IsBusy)
                    continue;

                if (n < openFileDialog.FileNames.Length)
                {
                    // Load the image and add the filename to the list of arguments
                    Bitmap image = new Bitmap(openFileDialog.FileNames[n]);
                    List<object> arguments = new List<object> { image, openFileDialog.FileNames[n] };

                    worker.RunWorkerAsync(arguments);
                    n++;
                }
                else
                {
                    break;
                }
            }
        }

        // Worker's DoWork event
        private void Worker_DoWork(object sender, DoWorkEventArgs e)
        {
            List<object> arguments = (List<object>) e.Argument;
            Bitmap image = (Bitmap) arguments[0];
            string filename = (string) arguments[1];
            int width = image.Width;
            int height = image.Height;

            ProgressDelegate progress = UpdateProgressBar;
            SortedList<string, object> parametrs = new SortedList<string, object>
            {
