// Функция для создания панели с гистограммой
private JPanel createHistogramPanel(int[] histogram, String label) {
    JPanel histogramPanel = new JPanel() {
        @Override
        protected void paintComponent(Graphics g) {
            super.paintComponent(g);
            int max = Arrays.stream(histogram).max().getAsInt();
            for (int i = 0; i < 256; i++) {
                int height = (int) (300.0 * histogram[i] / max);
                g.fillRect(i, 300 - height, 1, height);
            }
        }
    };
    histogramPanel.setPreferredSize(new Dimension(256, 300));
    histogramPanel.setBorder(BorderFactory.createTitledBorder(label));
    return histogramPanel;
}

// Функция для получения гистограммы изображения
private int[] getHistogram(BufferedImage image) {
    int[] histogram = new int[256];
    for (int i = 0; i < image.getWidth(); i++) {
        for (int j = 0; j < image.getHeight(); j++) {
            int pixel = image.getRGB(i, j);
            int red = (pixel >> 16) & 0xff;
            int green = (pixel >> 8) & 0xff;
            int blue = pixel & 0xff;
            int brightness = (int) (0.2126 * red + 0.7152 * green + 0.0722 * blue);
            if (brightness >= 0 && brightness < 256) {
                histogram[brightness]++;
            }
        }
    }
    return histogram;
}

// Функция пилообразного контрастирования с порогами
private BufferedImage sawtoothContrast(BufferedImage image, float t1, float t2) {
    BufferedImage output = new BufferedImage(image.getWidth(), image.getHeight(), image.getType());
    for (int i = 0; i < image.getWidth(); i++) {
        for (int j = 0; j < image.getHeight(); j++) {
            int pixel = image.getRGB(i, j);
            int red = (pixel >> 16) & 0xff;
            int green = (pixel >> 8) & 0xff;
            int blue = pixel & 0xff;
            int brightness = (int) (0.2126 * red + 0.7152 * green + 0.0722 * blue);
            if (brightness < t1) {
                output.setRGB(i, j, new Color(0, 0, 0).getRGB());
            } else if (brightness >= t1 && brightness < t2) {
                int value = (int) (255 * (brightness - t1) / (t2 - t1));
                output.setRGB(i, j, new Color(value, value, value).getRGB());
            } else if (brightness >= t2) {
                output.setRGB(i, j, new Color(255, 255, 255).getRGB());
            }
        }
    }
    return output;
}

