// Функция пилообразного контрастирования с порогами и заданным направлением
private BufferedImage sawtoothContrast(BufferedImage image, float t1, float t2, boolean up, boolean down) {
BufferedImage output = new BufferedImage(image.getWidth(), image.getHeight(), image.getType());
for (int i = 0; i < image.getWidth(); i++) {
for (int j = 0; j < image.getHeight(); j++) {
int pixel = image.getRGB(i, j);
int red = (pixel >> 16) & 0xff;
int green = (pixel >> 8) & 0xff;
int blue = pixel & 0xff;
int brightness = (int) (0.2126 * red + 0

.7152 * green + 0.0722 * blue); // вычисление яркости пикселя
if (up && brightness >= t2) { // если выбрано направление повышения контраста и яркость выше второго порога
int newBrightness = (int) (255 * ((float) (brightness - t2) / (255 - t2))); // вычисляем новое значение яркости пикселя
red = Math.min(255, Math.max(0, (int) (red * ((float) newBrightness / brightness)))); // корректируем цветовые компоненты
green = Math.min(255, Math.max(0, (int) (green * ((float) newBrightness / brightness)))));
blue = Math.min(255, Math.max(0, (int) (blue * ((float) newBrightness / brightness)))));
}
else if (down && brightness <= t1) { // если выбрано направление понижения контраста и яркость ниже первого порога
int newBrightness = (int) (255 * ((float) brightness / t1)); // вычисляем новое значение яркости пикселя
red = Math.min(255, Math.max(0, (int) (red * ((float) newBrightness / brightness)))); // корректируем цветовые компоненты
green = Math.min(255, Math.max(0, (int) (green * ((float) newBrightness / brightness)))));
blue = Math.min(255, Math.max(0, (int) (blue * ((float) newBrightness / brightness)))));
}
output.setRGB(i, j, (red << 16) | (green << 8) | blue); // устанавливаем новый цвет пикселя в выходном изображении
}
}
return output; // возвращаем выходное изображение
}

