```java
import java.awt.image.BufferedImage;
import java.io.*;
import java.util.ArrayList;
import java.util.List;
import javax.imageio.ImageIO;

public class VideoEncoder {
    private static final int BLOCK_SIZE = 8; // 块大小
    private static final int BLOCK_THRESHOLD = 10; // 块比较阈值

    public static void main(String[] args) throws IOException {
        File file = new File("video.avi");
        List<BufferedImage> frames = loadVideoFrames(file);

        List<List<VideoBlock>> blocks = new ArrayList<>();
        for (BufferedImage frame : frames) {
            List<VideoBlock> frameBlocks = new ArrayList<>();
            for (int i = 0; i < frame.getWidth(); i += BLOCK_SIZE) {
                for (int j = 0; j < frame.getHeight(); j += BLOCK_SIZE) {
                    BufferedImage blockImage = frame.getSubimage(i, j, BLOCK_SIZE, BLOCK_SIZE);
                    VideoBlock block = new VideoBlock(blockImage);
                    frameBlocks.add(block);
                }
            }
            blocks.add(frameBlocks);
        }

        for (int i = 0; i < blocks.size() - 1; i++) {
            List<VideoBlock> currentFrame = blocks.get(i);
            List<VideoBlock> nextFrame = blocks.get(i + 1);
            for (int j = 0; j < currentFrame.size(); j++) {
                VideoBlock currentBlock = currentFrame.get(j);
                VideoBlock nextBlock = nextFrame.get(j);
                if (currentBlock.isSimilarTo(nextBlock, BLOCK_THRESHOLD)) {
                    currentBlock.setSimilar(true);
                    nextBlock.setSimilar(true);
                }
            }
        }

        List<Integer> compressedData = new ArrayList<>();
        for (List<VideoBlock> frameBlocks : blocks) {
            for (VideoBlock block : frameBlocks) {
                if (block.isSimilar()) {
                    compressedData.add(1);
                } else {
                    compressedData.add(0);
                    compressedData.addAll(block.getPixels());
                }
            }
        }

        File outputFile = new File("output.dat");
        writeCompressedData(outputFile, compressedData);
    }

    private static List<BufferedImage> loadVideoFrames(File file) throws IOException {
        List<BufferedImage> frames = new ArrayList<>();
        try (FileInputStream fis = new FileInputStream(file);
             BufferedInputStream bis = new BufferedInputStream(fis)) {
            while (true) {
                byte[] frameLengthBytes = new byte[4];
                int readLength = bis.read(frameLengthBytes);
                if (readLength != 4) {
                    break;
                }
                int frameLength = bytesToInt(frameLengthBytes);
                byte[] frameBytes = new byte[frameLength];
                readLength = bis.read(frameBytes);
                if (readLength != frameLength) {
                    break;
                }
                BufferedImage frame = ImageIO.read(new ByteArrayInputStream(frameBytes));
                frames.add(frame);
            }
        }
        return frames;
    }

    private static int bytesToInt(byte[] bytes) {
        int number = 0;
        for (int i = 0; i < 4; i++) {
            number |= bytes[i] & 0xff;
            if (i < 3) {
                number <<= 8;
            }
        }
        return number;
    }

    private static void writeCompressedData(File file, List<Integer> data) throws IOException {
        FileOutputStream fos = new FileOutputStream(file);
        BufferedOutputStream bos = new BufferedOutputStream(fos);
        for (int i = 0; i < data.size(); i += 8) {
            int b = 0;
            for (int j = 0; j < 8 && i + j < data.size(); j++) {
                if (data.get(i + j) == 1) {
                    b |= (1 << j);
                }
            }
            bos.write(b);
        }
        bos.close();
        fos.close();
    }
}

