                       aacD_codec_ctx->channel_layout, aacD_codec_ctx->sample_fmt, aacD_codec_ctx->sample_rate, 0, NULL);
    swr_init(swr_ctx);

    // 重采样音频并存储到新的帧中
    AVFrame *resampled_audio_frame = av_frame_alloc();
    resampled_audio_frame->channel_layout = g711aE_codec_ctx->channel_layout;
    resampled_audio_frame->sample_rate = g711aE_codec_ctx->sample_rate;
    resampled_audio_frame->format = g711aE_codec_ctx->sample_fmt;
    resampled_audio_frame->channels = g711aE_codec_ctx->channels;

    int samples_per_channel = swr_convert(
        swr_ctx,
        resampled_audio_frame->data,
        decoded_audio_frame->nb_samples,
        (const uint8_t**)decoded_audio_frame->data,
        decoded_audio_frame->nb_samples
    );

    resampled_audio_frame->nb_samples = samples_per_channel;

    // 编码解码后的音频帧
    avcodec_send_frame(g711aE_codec_ctx, resampled_audio_frame);
    AVPacket out_g711a_pkt;
    av_init_packet(&out_g711a_pkt);
    avcodec_receive_packet(g711aE_codec_ctx, &out_g711a_pkt);

    // 现在 out_h264_pkt 和 out_g711a_pkt 包含编码后的数据

    // 释放
    av_packet_unref(&h265_pkt);
    av_packet_unref(&aac_pkt);
    av_packet_unref(&out_h264_pkt);
    av_packet_unref(&out_g711a_pkt);

    av_frame_free(&decoded_frame);
    av_frame_free(&decoded_audio_frame);
    av_frame_free(&resampled_audio_frame);

    avcodec_close(h265D_codec_ctx);
    avcodec_close(aacD_codec_ctx);
    avcodec_close(h264E_codec_ctx);
    avcodec_close(g711aE_codec_ctx);
    avcodec_free_context(&h265D_codec_ctx);
    avcodec_free_context(&aacD_codec_ctx);
    avcodec_free_context(&h264E_codec_ctx);
    avcodec_free_context(&g711aE_codec_ctx);

    swr_close(swr_ctx);
    swr_free(&swr_ctx);

    return 0;
}
```

在此示例中，我们使用了较新的编码 API ：`avcodec_send_frame` 和 `avcodec_receive_packet`，并添加了重采样处理以将输入的 44.1KHz AAC 音频转换成 8KHz G.711a 音频。

为了进行重采样，我们创建了一个 `SwrContext`，并使用 `swr_alloc_set_opts` 设置重采样参数。 然后通过 `swr_init` 初始化重采样上下文。我们使用 `swr_convert` 函数将解码后的音频帧数据转换为 8KHz 并将结果存储到 `resampled_audio_frame` 中。最后我们将它发送到 G.711a 编码器并接收编码后的数据包。所有分配的内存在结束时均得到释放。

