static AVFormatContext *out_fmt_ctx = NULL;
static AVStream *out_video_stream = NULL;
static AVCodecContext *out_codec_ctx = NULL;
static AVFrame *out_frame = NULL;
static AVPacket *out_pkt = NULL;
