static int open_output_file(const char *filename, int width, int height)
{
    int i, ret;

    out_fmt_ctx = avformat_alloc_context();
    if (!out_fmt_ctx) {
        fprintf(stderr, "Could not allocate output format context\n");
        return -1;
    }

    out_fmt_ctx->oformat = av_guess_format(NULL, filename, NULL);
    if (!out_fmt_ctx->oformat) {
        fprintf(stderr, "Could not find output format\n");
        goto fail;
    }

    // Open the output file
    ret = avio_open(&out_fmt_ctx->pb, filename, AVIO_FLAG_WRITE);
    if (ret < 0) {
        fprintf(stderr, "Could not open output file '%s': %s\n", filename,
                av_err2str(ret));
        goto fail;
    }

    // Create the video stream
    out_video_stream = avformat_new_stream(out_fmt_ctx, NULL);
    if (!out_video_stream) {
        fprintf(stderr, "Could not create video stream\n");
        goto fail;
    }

    out_codec_ctx = out_video_stream->codec;
    out_codec_ctx->codec_id = out_fmt_ctx->oformat->video_codec;
    out_codec_ctx->codec_type = AVMEDIA_TYPE_VIDEO;

    // Set the output parameters (codec, width, height, etc.)
    out_codec_ctx->bit_rate = 400000; /* modify this if needed */
    out_codec_ctx->width = width;
    out_codec_ctx->height = height;
    out_codec_ctx->time_base = (AVRational){1, 25}; // 25 fps
    out_codec_ctx->gop_size = 12; /* emit one intra frame every twelve frames at most */
    out_codec_ctx->pix_fmt = AV_PIX_FMT_YUV420P;

    if (out_fmt_ctx->oformat->flags & AVFMT_GLOBALHEADER)
        out_codec_ctx->flags |= CODEC_FLAG_GLOBAL_HEADER;

    AVCodec *codec = avcodec_find_encoder(out_codec_ctx->codec_id);
    if (!codec) {
        fprintf(stderr, "Codec not found\n");
        goto fail;
    }

    // Open the encoder
    ret = avcodec_open2(out_codec_ctx, codec, NULL);
    if (ret < 0) {
        fprintf(stderr, "Could not open encoder\n");
        goto fail;
    }

    // Allocate and initialize the video frame and packet
    out_frame = av_frame_alloc();
    if (!out_frame) {
        fprintf(stderr, "Could not allocate video frame\n");
        goto fail;
    }

    out_pkt = av_packet_alloc();
    if (!out_pkt) {
        fprintf(stderr, "Could not allocate packet\n");
        goto fail;
    }

    out_buffer = calloc(1, out_codec_ctx->width * out_codec_ctx->height * 3 / 2);
    if (!out_buffer) {
        fprintf(stderr, "Could not allocate buffer\n");
        goto fail;
    }

    av_image_fill_arrays(out_frame->data, out_frame->linesize, out_buffer,
                          out_codec_ctx->pix_fmt, out_codec_ctx->width, out_codec_ctx->height, 1);

    sws_ctx = sws_getContext(width, height, AV_PIX_FMT_BGRA,
                             out_codec_ctx->width, out_codec_ctx->height, out_codec_ctx->pix_fmt,
                             SWS_BILINEAR, NULL, NULL, NULL);
    if (!sws_ctx) {
        fprintf(stderr, "Could not initialize sws context\n");
        goto fail;
    }

    av_dump_format(out_fmt_ctx, 0, filename, 1);

    // Write the stream header, if any
    ret = avformat_write_header(out_fmt_ctx, NULL);
    if (ret < 0) {
        fprintf(stderr, "Error occurred when writing header: %s\n", av_err2str(ret));
        goto fail;
    }

    return 0;

fail:
    if (out_fmt_ctx)
        avformat_free_context(out_fmt_ctx);
    if (rgb_frame)
        av_frame_free(&rgb_frame);
    if (out_frame)
        av_frame_free(&out_frame);
    if (out_pkt)
        av_packet_free(&out_pkt);
    if (out_buffer)
        free(out_buffer);
    if (sws_ctx)
        sws_freeContext(sws_ctx);
    return -1;
}

static int write_video_frame(uint8_t *data, int width, int height, int stride, int64_t pts)
{
    int ret;

    av_image_fill_arrays(rgb_frame->data, rgb_frame->linesize, data,
                          AV_PIX_FMT_BGRA, width, height, stride);
    sws_scale(sws_ctx, (const uint8_t * const *)rgb_frame->data, rgb_frame->linesize,
              0, height, out_frame->data, out_frame->linesize);

    out_frame->pts = pts;

    ret = avcodec_send_frame(out_codec_ctx, out_frame);
    if (ret < 0) {
        fprintf(stderr, "Error when sending frame to encoder: %s\n", av_err2str(ret));
        return -1;
    }

    while (ret >= 0) {
        ret = avcodec_receive_packet(out_codec_ctx, out_pkt);
        if (ret == AVERROR(EAGAIN) || ret == AVERROR_EOF)
            return 0;
        else if (ret < 0) {
            fprintf(stderr, "Error encoding video frame: %s\n", av_err2str(ret));
            return -1;
        }

        av_packet_rescale_ts(out_pkt, out_codec_ctx->time_base, out_video_stream->time_base);
        out_pkt->stream_index = out_video_stream->index;

        ret = av_interleaved_write_frame(out_fmt_ctx, out_pkt);
        if (ret < 0) {
            fprintf(stderr, "Error while writing video frame: %s\n", av_err2str(ret));
            return -1;
        }

        av_packet_unref(out_pkt);
    }

    return 0;
}

int main(int argc, char *argv[])
{
