    uint64_t pts;
    struct stat s;
    void *data;

    if (argc < 5) {
        fprintf(stderr, "Usage: %s <device> <width> <height> <output>\n", argv[0]);
        exit(1);
    }

    fd = open(argv[1], O_RDONLY);
    if (fd < 0) {
        fprintf(stderr, "Could not open device '%s': %s\n", argv[1], strerror(errno));
        exit(1);
    }

    width = atoi(argv[2]);
    height = atoi(argv[3]);

    if (open_output_file(argv[4], width, height) < 0)
        exit(1);

    fd = open(argv[1], O_RDONLY);
    if (fd < 0) {
        fprintf(stderr, "Could not open device '%s': %s\n", argv[1], strerror(errno));
        exit(1);
    }

    if (fstat(fd, &s) < 0) {
        fprintf(stderr, "Could not get file size\n");
        exit(1);
    }

    stride = width * 4;
    data = mmap(NULL, s.st_size, PROT_READ, MAP_PRIVATE, fd, 0);
    if (!data) {
        fprintf(stderr, "Could not mmap device memory: %s\n", strerror(errno));
        exit(1);
    }

    rgb_frame = av_frame_alloc();
    if (!rgb_frame) {
        fprintf(stderr, "Could not allocate RGB frame\n");
        exit(1);
    }

    rgb_frame->width = width;
    rgb_frame->height = height;
    rgb_frame->format = AV_PIX_FMT_BGRA;

    ret = av_image_alloc(rgb_frame->data, rgb_frame->linesize, width, height, AV_PIX_FMT_BGRA, 32);
    if (ret < 0) {
        fprintf(stderr, "Could not allocate RGB image: %s\n", av_err2str(ret));
        exit(1);
    }

    pts = 0;
    while (1) {
        if (write_video_frame(data, width, height, stride, pts) < 0)
            break;
        pts += 1;
    }

    av_write_trailer(out_fmt_ctx);

    if (rgb_frame)
        av_frame_free(&rgb_frame);
    if (out_codec_ctx)
        avcodec_close(out_codec_ctx);
    if (out_fmt_ctx)
        avio_closep(&out_fmt_ctx->pb);
    if (out_fmt_ctx)
        avformat_free_context(out_fmt_ctx);
    if (out_frame)
        av_frame_free(&out_frame);
    if (out_pkt)
        av_packet_free(&out_pkt);
    if (out_buffer)
        free(out_buffer);
    if (sws_ctx)
        sws_freeContext(sws_ctx);

    return 0;
}
