using System;

namespace Chess
{
    public class Chess
    {
        public static bool King(int x1, int y1, int x2, int y2)
        {
            if (Math.Abs(x1 - x2) <= 1 && Math.Abs(y1 - y2) <= 1)
            {
                return true;
            }
            return false;
        }

        public static bool Queen(int x1, int y1, int x2, int y2)
        {
            if (Rook(x1, y1, x2, y2) || Bishop(x1, y1, x2, y2))
            {
                return true;
            }
            return false;
        }

        public static bool Rook(int x1, int y1, int x2, int y2)
        {
            if ((x1 == x2) || (y1 == y2))
            {
                return true;
            }
            return false;
        }

        public static bool Bishop(int x1, int y1, int x2, int y2)
        {
            if (Math.Abs(x1 - x2) == Math.Abs(y1 - y2))
            {
                return true;
            }
            return false;
        }

        public static bool Knight(int x1, int y1, int x2, int y2)
        {
            if ((Math.Abs(x1 - x2) == 1 && Math.Abs(y1 - y2) == 2) || (Math.Abs(x1 - x2) == 2 && Math.Abs(y1 - y2) == 1))
            {
                return true;
            }
            return false;
        }

        public static bool Pawn(int x1, int y1, int x2, int y2)
        {
            if (x1 == x2 && (y1 - y2 == 1 || y2 - y1 == 1))
            {
                return true;
            }
            return false;
        }
    }
}
