#include <stdlib.h>

int main()
{
    int limit = 1000000;
    int* table = (int*)malloc(sizeof(int) * (limit + 1));
    int i, j;
    //初始化素数表
    for (i = 2; i <= limit; i++)
    {
        table[i] = 1;
    }
    //筛选素数
    for (i = 2; i <= limit / 2; i++)
    {
        if (table[i] == 1)
        {
            for (j = i * 2; j <= limit; j += i)
            {
                table[j] = 0;
            }
        }
