                BlockTable blockTable = (BlockTable)tr.GetObject(db.BlockTableId, OpenMode.ForRead);
                BlockTableRecord modelSpace = (BlockTableRecord)tr.GetObject(blockTable[BlockTableRecord.ModelSpace], OpenMode.ForRead);

                int layoutCount = 1;

                foreach (ObjectId blockId in modelSpace)
                {
                    DBObject obj = tr.GetObject(blockId, OpenMode.ForRead);
                    if (obj is BlockReference)
                    {
                        BlockReference blockRef = (BlockReference)obj;
                        BlockTableRecord blockRecord = (BlockTableRecord)tr.GetObject(blockRef.BlockTableRecord, OpenMode.ForRead);

                        if (blockRecord.Name == blockName)
                        {
                            LayoutManager layoutManager = LayoutManager.Current;
                            string layoutName = "Layout_" + blockName + "_" + layoutCount;
                            ObjectId layoutId = layoutManager.CreateLayout(layoutName);
                            Layout layout = (Layout)tr.GetObject(layoutId, OpenMode.ForWrite);

                            ViewportTableRecord viewportRec = new ViewportTableRecord();
                            tr.AddNewlyCreatedDBObject(viewportRec, true);

                            BlockTableRecord currentBTR = (BlockTableRecord)tr.GetObject(layout.BlockTableRecordId, OpenMode.ForWrite);
                            Viewport viewportEntity = new Viewport();
                            viewportEntity.ViewportObjectId = viewportRec.ObjectId;
                            viewportEntity.CenterPoint = new Point3d(layout.PlotPaperSize.X / 2, layout.PlotPaperSize.Y / 2, 0);

                            currentBTR.AppendEntity(viewportEntity);
                            tr.AddNewlyCreatedDBObject(viewportEntity, true);

                            Extents3d blockExtents = blockRef.GeometricExtents;
                            double width = blockExtents.MaxPoint.X - blockExtents.MinPoint.X;
                            double height = blockExtents.MaxPoint.Y - blockExtents.MinPoint.Y;

                            double paperWidth = layout.PlotPaperSize.X - layout.PlotPaperMargins.MinPoint.X - layout.PlotPaperMargins.MaxPoint.X;
                            double paperHeight = layout.PlotPaperSize.Y - layout.PlotPaperMargins.MinPoint.Y - layout.PlotPaperMargins.MaxPoint.Y;
                            double scaleX = paperWidth / width;
                            double scaleY = paperHeight / height;

                            double scale = Math.Min(scaleX, scaleY);
                            viewportRec.Width = width * scale;
                            viewportRec.Height = height * scale;
                            viewportRec.ViewCenter = new Point2d(blockExtents.MinPoint.X + width / 2, blockExtents.MinPoint.Y + height / 2);
                            viewportRec.ViewTarget = blockExtents.MinPoint.Add(new Vector3d(width / 2, height / 2, 0));

                            layoutCount++;
                        }
                    }
                }

                tr.Commit();
                ed.Regen();
            }
            ed.WriteMessage("\nCreated layouts and set viewport zoom to block extents for block: " + blockName);
        }
    }
}
```

