function x = jacobi_iteration(A, B, tolerance, max_iterations)
    n = length(B);
    x = zeros(n, 1);
    x_prev = x;

    for k = 1:max_iterations
        for i = 1:n
            x(i) = (B(i) - A(i, [1:i-1, i+1:n]) * x_prev([1:i-1, i+1:n])) / A(i, i);
