                if new_cost < fireflies{i}.cost
                    fireflies{i}.path = new_path;
                    fireflies{i}.grocery = new_grocery;
                    fireflies{i}.cost = new_cost;
                end
            end
        end
    end

    % Find best solution
    [~, best_idx] = min([fireflies{:}.cost]);
    current_best_cost = fireflies{best_idx}.cost;

    if current_best_cost < best_cost
        best_cost = current_best_cost;
        best_path = fireflies{best_idx}.path;
        best_grocery = fireflies{best_idx}.grocery;
        stall_count = 0;
    else
        stall_count = stall_count + 1;
    end

    % Check stopping condition
    if stall_count >= n_stall
        break;
    end
end

end

function cost = calculate_cost(path, grocery, distance_matrix, attraction_matrix)
    cost = sum(distance_matrix(sub2ind(size(distance_matrix), path, [path(2:end), path(1)]))) + ...
           sum(attraction_matrix(:, 1)' .* ~grocery' .* path) + ...
           sum(attraction_matrix(:, 2)' .* grocery' .* (1 - path));
end

function r = calculate_distance(path1, path2, distance_matrix)
    r = sum(diag(distance_matrix(path1, path2)));
end

function new_path = update_path(path1, path2, alpha, beta)
    % This is a rough example, you may want to implement a more sophisticated path update mechanism
    new_path = path1;

    for i = 1:length(path1)
        if rand < beta
            idx = find(path2 == path1(i));
            new_path([i, idx]) = new_path([idx, i]);
        end
    end
end

