function [x,err,fig] = solve_LS(A, b)

  % Check if A has full row rank, otherwise use pseudoinverse
  if rank(A) == size(A, 1)
    x = A \ b;
  else
    x = pinv(A) * b;
  end

  % Compute error vector Ax-b
  err = A * x - b;

  % Create figure
  fig = figure;

  % Plot black dots
  scatter(1:length(x), err, 20, 'k', 'filled');
  hold on;

  % Plot blue lines (use for loop)
  for i = 1:length(x)
    line([i, i], [0, err(i)], 'Color', 'b', 'LineStyle', '--');
  end

  % Plot red dotted line
  yline(0, 'r', '--');

  % Set limits of the axes
  xlim([0, length(x)+1]);
  ylim([min(err)-1, max(err)+1]);

  % Label the axes and title
  xlabel('x');
  ylabel('Ax-b');
  title('Error computed from Ax-b');

  % Turn off hold
  hold off;

end

